function getHandleCount()
{
    var ret = 0;
    switch(process.platform)
    {
        case 'win32':
            var h = kernel32.GetCurrentProcess();
            var c = require('_GenericMarshal').CreateVariable(4);
            kernel32.GetProcessHandleCount(h, c);
            ret = c.toBuffer().readUInt32LE();
            break;
        default:
            break;
    }
    return (ret);
}

